import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, View,TextInput,TouchableOpacity,Image} from 'react-native';
import { SafeAreaView } from 'react-native-web';
const open = require("./assets/open.png");


export default function App() {
  return (
    <SafeAreaView>
      <Image source={open} style={styles.image} />
      <Text style={styles.text}>Image Generator</Text>
      <View style={styles.inputs}>
          <TextInput
            style={styles.input}
            placeholder="Ingresa un texto"
          />
          <TouchableOpacity
            style={styles.button}>
            <Text style={styles.buttonText}>Presionar</Text>
          </TouchableOpacity>
      </View>

    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#455546',
    alignItems: 'center',
    justifyContent: 'flex-start',
  },
  text:{
    fontSize:43,
    fontWeight:'bold',
    color:'#fff',
    marginTop:152,
    marginBottom:22

  },
    input: {
    width: '60%',
    height: 40,
    borderColor: 'gray',
    borderWidth: 1,
    color:'#fff',
    borderRadius: 5,
    paddingHorizontal: 10,

  },
  button: {
    backgroundColor: 'gray',
    paddingHorizontal: 15,
    paddingVertical: 10,
    borderRadius: 5,
  },
  buttonText: {
    color: 'white',
    fontSize: 16,
    fontWeight: 'bold',
  },
  inputs:{
        width:'100%',
    flexDirection: "row",
    justifyContent: "center",
    flexWrap: "wrap",
    alignItems:'center'
  },
  image:{
    width: "100%",
    height: 340,
    position: "absolute",
    top: 0,
    resizeMode: 'cover',
  }
});
